import React, { Component } from "react";
import "./CertificationCard.css";
import { Fade } from "react-reveal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faJs as jsIcon,
  faNodeJs as nodeJsIcon,
  faMicrosoft as microsoftIcon,
  faReact as reactIcon,
  faLaravel as laravelIcon,
  faPhp as phpIcon,
  faCss3Alt as cssIcon,
  faDatabase as sqlIcon,
  faHtml5 as html5Icon,
  faDocker as mongodbIcon, // Using docker icon as placeholder for MongoDB
} from "@fortawesome/free-brands-svg-icons";

class CertificationCard extends Component {
  getIcon = (iconName) => {
    switch (iconName) {
      case "js":
        return jsIcon;
      case "node-js":
        return nodeJsIcon;
      case "microsoft":
        return microsoftIcon;
      case "react":
        return reactIcon;
      case "laravel":
        return laravelIcon;
      case "php":
        return phpIcon;
      case "css3":
        return cssIcon;
      case "sql":
        return sqlIcon;
      case "html5":
        return html5Icon;
      case "mongodb":
        return mongodbIcon;
      default:
        return null;
    }
  };

  render() {
    const { certificate, theme } = this.props;
    const icon = this.getIcon(certificate.icon_name);

    return (
      <Fade bottom duration={2000} distance="20px">
        <div className="cert-card">
          <div className="content">
            <a
              href={certificate.certificate_link}
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="content-overlay"></div>
              <div
                className="cert-header"
                style={{ backgroundColor: certificate.color_code }}
              >
                {icon && (
                  <FontAwesomeIcon
                    className="logo_icon"
                    icon={icon}
                    size="3x"
                    style={{ color: "#fff" }}
                  />
                )}
              </div>
              <div className="content-details fadeIn-top">
                <h3 className="content-title" style={{ color: theme.body }}>
                  Certificate
                </h3>
              </div>
            </a>
          </div>
          <div className="cert-body">
            <h2 className="cert-body-title" style={{ color: theme.text }}>
              {certificate.title}
            </h2>
            <h3
              className="cert-body-subtitle"
              style={{ color: theme.secondaryText }}
            >
              {certificate.subtitle}
            </h3>
          </div>
        </div>
      </Fade>
    );
  }
}

export default CertificationCard;
