// In CertificationCard.jsx

import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faJs, faNodeJs, faLaravel, faPhp, faMysql, faHtml5, faCss3, faReact, faMongoDB, faMicrosoft } from '@fortawesome/free-brands-svg-icons'; // Import required icons

const icons = {
  js: faJs,
  "node-js": faNodeJs,
  laravel: faLaravel,
  php: faPhp,
  mysql: faMysql,
  html5: faHtml5,
  css3: faCss3,
  react: faReact,
  mongodb: faMongoDB,
  microsoft: faMicrosoft
};

const CertificationCard = ({ certificate, theme }) => {
  const { title, subtitle, icon_name, certificate_link, alt_name, color_code, skills } = certificate;
  const icon = icons[icon_name];

  return (
    <div className="certification-card" style={{ borderColor: color_code }}>
      <a href={certificate_link} target="_blank" rel="noopener noreferrer">
        <div className="certification-icon" style={{ color: color_code }}>
          <FontAwesomeIcon icon={icon} size="2x" />
        </div>
        <div className="certification-details">
          <h2 className="certification-title" style={{ color: theme.text }}>
            {title}
          </h2>
          <p className="certification-subtitle" style={{ color: theme.text }}>
            {subtitle}
          </p>
          <p className="certification-alt-name" style={{ color: theme.text }}>
            {alt_name}
          </p>
          <div className="certification-skills">
            <strong>Skills:</strong>
            <ul>
              {skills.map((skill, index) => (
                <li key={index} style={{ color: theme.text }}>{skill}</li>
              ))}
            </ul>
          </div>
        </div>
      </a>
    </div>
  );
};

export default CertificationCard;
