import React, { Component } from "react";
import "./CertificationCard.css";
import { Fade } from "react-reveal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faJs as jsIcon, faNodeJs as nodeJsIcon, faMicrosoft as microsoftIcon, faLaravel as laravelIcon, faMysql as mysqlIcon, faCss3 as css3Icon, faHtml5 as html5Icon, faPhp as phpIcon, faMongoDB as mongodbIcon, faSql as sqlIcon } from '@fortawesome/free-brands-svg-icons';

class CertificationCard extends Component {
  getIcon(iconName) {
    const icons = {
      js: jsIcon,
      'node-js': nodeJsIcon,
      microsoft: microsoftIcon,
      laravel: laravelIcon,
      mysql: mysqlIcon,
      css3: css3Icon,
      html5: html5Icon,
      php: phpIcon,
      mongodb: mongodbIcon,
      sql: sqlIcon
    };

    return icons[iconName] || jsIcon; // default to JS icon if not found
  }

  render() {
    const { certificate, theme } = this.props;
    const icon = this.getIcon(certificate.icon_name);

    return (
      <Fade bottom duration={2000} distance="20px">
        <div className="cert-card">
          <div className="content">
            <a
              href={certificate.certificate_link}
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="content-overlay"></div>
              <div
                className="cert-header"
                // style={{ backgroundColor: certificate.color_code }}
              >
                <FontAwesomeIcon
                  className="logo_icon"
                  icon={icon}
                  size="3x"
                />
              </div>
              <div className="content-details fadeIn-top">
                <h3 className="content-title" style={{ color: theme.body }}>
                  Certificate
                </h3>
              </div>
            </a>
          </div>
          <div className="cert-body">
            <h2 className="cert-body-title" style={{ color: theme.text }}>
              {certificate.title}
            </h2>
            <h3
              className="cert-body-subtitle"
              style={{ color: theme.secondaryText }}
            >
              {certificate.subtitle}
            </h3>
          </div>
        </div>
      </Fade>
    );
  }
}

export default CertificationCard;
